import { useState } from 'react';
import './index.css'
import Modal from './Modal'

function App() {

  const [modal,showModal]=useState(false);

  const displayModal=()=>{
      showModal(!modal) 
  }
  const closeModal=()=>{
    showModal(false) 
  }
  return (
    <div className="App">
      <h1 className="text-green-400 text-4xl">Project Planner</h1>
      <button onClick={displayModal} className="px-6 py-1 bg-green-400 text-white">Click Me</button>
      <button onClick={closeModal}>Close</button>
      {modal &&<Modal />}

    </div>
  );
}

export default App;
