import { useEffect, useState } from 'react';
import './index.css'
import Modal from './Modal'
import axios from 'axios'

function App() {

  const [modal,showModal]=useState(false);
  const [projects,setProjects]=useState();
  // const [error, setError] = useState(null);

  const displayModal=()=>{
      showModal(!modal) 
  }
  const closeModal=()=>{
    showModal(false) 
  }
  useEffect(()=>{
    axios.get('http://localhost:8000/blogs')
    .then((res)=>{
      setProjects(res.data)
    }).catch(err=>{
      console.log(err.message)
      // setError(err.message)
    })
  },[])
  return (
    <div className="App">
      <h1 className="text-green-400 text-4xl">Project Planner</h1>
      <button onClick={displayModal} className="px-6 py-1 bg-green-400 text-white">Click Me</button>
      <button  className="px-6 py-1 bg-green-400 text-white" onClick={closeModal}>Close</button>
      {modal &&<Modal />}

    </div>
  );
}

export default App;
